import React, { useState } from 'react';
import styled from 'styled-components';

const ProjectListContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 20px;
`;

const NewProjectForm = styled.div`
    margin-top: 20px;
`;

const Card = styled.div`
    border: 1px solid #ccc;
    border-radius: 8px;
    padding: 20px;
    width: 300px;
    cursor: pointer;
    box-shadow: 2px 2px 12px rgba(0, 0, 0, 0.1);
    transition: box-shadow 0.3s ease;
    &:hover {
        box-shadow: 2px 2px 20px rgba(0, 0, 0, 0.2);
    }
`;

function ProjectList({ projects, addProject, removeProject, selectProject }) {
    const [newProject, setNewProject] = useState({ titre: '', description: '', details: [] });

    const handleAddProject = () => {
        addProject(newProject);
        setNewProject({ titre: '', description: '', details: [] });
    };

    return (
        <div>
            <h2>Liste des Projets</h2>
            <ProjectListContainer>
                {Object.entries(projects).map(([id, project]) => (
                    <Card key={id} onClick={() => selectProject(id)}>
                        <h3>{project.titre}</h3>
                        <p>{project.description}</p>
                    </Card>
                ))}
            </ProjectListContainer>
            <NewProjectForm>
                <h3>Ajouter un nouveau projet</h3>
                <input 
                    type="text" 
                    placeholder="Titre" 
                    value={newProject.titre} 
                    onChange={(e) => setNewProject({ ...newProject, titre: e.target.value })}
                />
                <input 
                    type="text" 
                    placeholder="Description" 
                    value={newProject.description} 
                    onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
                />
                <button onClick={handleAddProject}>Ajouter le projet</button>
            </NewProjectForm>
        </div>
    );
}

export default ProjectList;
