import React, { useState, useEffect } from 'react';
import ProjectList from './components/ProjectList';
import ProjectDetailsModal from './components/ProjectDetailsModal';
import ProjectManager from './ProjectManager';
import axios from 'axios';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // Assurez-vous que le root est configuré dans index.js


function App() {
    const [projects, setProjects] = useState({});
    const [selectedProject, setSelectedProject] = useState(null);
    const [modalIsOpen, setModalIsOpen] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:5000/projects')
            .then(response => {
                setProjects(response.data);
            })
            .catch(error => {
                console.error('Error fetching projects:', error);
            });
    }, []);

    return (
        <div>
            <h1>Project List</h1>
            {Object.keys(projects).map(projectId => (
                <ProjectManager key={projectId} project={{ id: projectId, ...projects[projectId] }} />
            ))}
        </div>
    );

    useEffect(() => {
        axios.get('http://localhost:5000/projects')
            .then(response => setProjects(response.data))
            .catch(error => console.error(error));
    }, []);

    const addProject = (newProject) => {
        axios.post('http://localhost:5000/projects', newProject)
            .then(response => {
                const projectId = response.data;
                setProjects({ ...projects, [projectId]: { ...newProject, images: [] } });
            })
            .catch(error => console.error(error));
    };

    const removeProject = (projectId) => {
        axios.delete(`http://localhost:5000/projects/${projectId}`)
            .then(() => {
                const updatedProjects = { ...projects };
                delete updatedProjects[projectId];
                setProjects(updatedProjects);
                setSelectedProject(null); // Clear selection if deleted
            })
            .catch(error => console.error(error));
    };

    const updateProject = (projectId, updatedProject) => {
        axios.put(`http://localhost:5000/projects/${projectId}`, updatedProject)
            .then(() => {
                const updatedProjects = { ...projects, [projectId]: updatedProject };
                setProjects(updatedProjects);
            })
            .catch(error => console.error(error));
    };

    const updateProjectImages = (projectId, images) => {
        const updatedProjects = { ...projects };
        updatedProjects[projectId].images = images;
        setProjects(updatedProjects);
    };

    const selectProject = (projectId) => {
        setSelectedProject(projectId);
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
    };

    return (
        <div className="App">
            <h1>Gestion de la Collection d'Images</h1>
            <ProjectList 
                projects={projects} 
                addProject={addProject}
                removeProject={removeProject}
                selectProject={selectProject}
            />
            {selectedProject && (
                <Modal 
                    isOpen={modalIsOpen}
                    onRequestClose={closeModal}
                    contentLabel="Détails du Projet"
                    className="Modal"
                    overlayClassName="Overlay"
                >
                    <ProjectDetailsModal 
                        project={projects[selectedProject]}
                        projectId={selectedProject}
                        updateProject={updateProject}
                        removeProject={removeProject}
                        updateProjectImages={updateProjectImages}
                        closeModal={closeModal}
                    />
                </Modal>
            )}
        </div>
    );
}

export default App;
