import React, { useState } from 'react';
import axios from 'axios';

const ProjectManager = ({ project }) => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [title, setTitle] = useState(project.titre);
    const [description, setDescription] = useState(project.description);
    const [details, setDetails] = useState(project.details.join(', '));

    const handleFileChange = (event) => {
        setSelectedFile(event.target.files[0]);
    };

    const handleFileUpload = () => {
        if (!selectedFile) {
            console.error('No file selected');
            return;
        }

        const formData = new FormData();
        formData.append('image', selectedFile);
        formData.append('projectId', project.id);

        axios.post('http://localhost:5000/upload', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        .then(response => {
            console.log('Image uploaded successfully:', response.data);
            // Handle the response, e.g., update the project state to include the new image
        })
        .catch(error => {
            console.error('Error uploading image:', error);
        });
    };

    const handleUpdateProject = () => {
        const updatedProject = {
            titre: title,
            description: description,
            details: details.split(',').map(detail => detail.trim()),
            images: project.images,
        };

        axios.put(`http://localhost:5000/projects/${project.id}`, updatedProject)
            .then(response => {
                console.log('Project updated successfully:', response.data);
            })
            .catch(error => {
                console.error('Error updating project:', error);
            });
    };

    return (
        <div>
            <h2>{title}</h2>
            <p>{description}</p>
            <input 
                type="text" 
                value={title} 
                onChange={(e) => setTitle(e.target.value)} 
                placeholder="Title"
            />
            <textarea 
                value={description} 
                onChange={(e) => setDescription(e.target.value)} 
                placeholder="Description"
            />
            <input 
                type="text" 
                value={details} 
                onChange={(e) => setDetails(e.target.value)} 
                placeholder="Details (comma separated)"
            />
            <button onClick={handleUpdateProject}>Update Project</button>
            
            <h3>Upload Image</h3>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleFileUpload}>Upload Image</button>

            <h3>Images</h3>
            {project.images && project.images.map((image, index) => (
                <img key={index} src={image} alt={`Project ${project.id} Image`} width="100" />
            ))}
        </div>
    );
};

export default ProjectManager;
