import React from 'react';
import ImageUploader from './ImageUploader';

function Project({ id, project, removeProject, updateProjectImages }) {
  const handleRemoveProject = () => {
    removeProject(id);
  };

  const handleUpdateImages = (images) => {
    updateProjectImages(id, images);
  };

  return (
    <div>
      <h3>{project.titre}</h3>
      <p>{project.description}</p>
      <p>DÃ©tails: {project.details.join(', ')}</p>
      <ImageUploader projectId={id} images={project.images || []} onUpdateImages={handleUpdateImages} />
      <button onClick={handleRemoveProject}>Supprimer le projet</button>
    </div>
  );
}

export default Project;
